sourcesContent:
import { Router } from "express";
import {
  create,
  getAll,
  getDetail,
  update,
  remove,
} from "../controllers/productCategory";
import { checkPermission } from "../middlewares/checkPermission";

const routerProductCategory = Router();

routerProductCategory.post("/",checkPermission, create);
routerProductCategory.get("/", getAll);
routerProductCategory.get("/:id", getDetail);
routerProductCategory.put("/:id", checkPermission, update);
routerProductCategory.delete("/:id", checkPermission, remove);

export default routerProductCategory;

