sourcesContent:
import ProductCategory from "../models/ProductCategory";

export const create = async (req, res) => {
  try {
    //Validate here 
    
    const data = await ProductCategory.create(req.body);
    if (!data) {
      throw new Error("Failed !");
    }
    return res.status(200).json({
      message: "Product created successfully",
      data,
    });
  } catch (error) {
    return res.json({
      name: error.name,
      message: error.message,
    });
  }
};
export const getAll = async (req, res) => {
  try {
    const data = await ProductCategory.findAll(req.body);
    if (!data) {
      throw new Error("Failed !");
    }
    return res.status(200).json({
      message: "Product created successfully",
      data,
    });
  } catch (error) {
    return res.json({
      name: error.name,
      message: error.message,
    });
  }
};
export const getDetail = async (req, res) => {
  try {
    const data = await ProductCategory.findByPk(req.params.id);
    if (!data) {
      throw new Error("Failed !");
    }
    return res.status(200).json({
      message: "Category created successfully",
      data,
    });
  } catch (error) {
    return res.json({
      name: error.name,
      message: error.message,
    });
  }
};
export const update = async (req, res) => {
  try {
    const data = await ProductCategory.findByPk(req.params.id, { new: true });
    if (!data) {
      throw new Error("Category not found!");
    }
    // Cập nhật dữ liệu mới vào category
    await data.update(req.body);
    return res.status(200).json({
      message: "Category updated successfully",
      data,
    });
  } catch (error) {
    return res.json({
      name: error.name,
      message: error.message,
    });
  }
};
export const remove = async (req, res) => {
  try {
    const data = await ProductCategory.findByPk(req.params.id);
    if (!data) {
      throw new Error("Category not found!");
    }
    // Xóa category
    await data.destroy();
    return res.status(200).json({
      message: "Category deleted successfully",
      data,
    });
  } catch (error) {
    return res.json({
      name: error.name,
      message: error.message,
    });
  }
};

