sourcesContent:
import { Sequelize } from "sequelize";
import { sequelize } from "../config/connectDatabase";

import ProductCategory from "./ProductCategory";
import Discount from "./discount";
import Product from "./product";
import ProductImages from "./ProductImages";
import ProductColor from "./ProductColor";
import ProductSize from "./ProductSize";
import ProductVariant from "./ProductVariant";
import User from "./User";
import Order from "./order";
import OrderDetail from "./OrderDetail";
import CustomerAddress from "./CustomerAddress";
import CustomerPayment from "./CustomerPayment";
import Blog from "./Blog";
import Wishlist from "./wishlist";
import ProductReview from "./productReview";

// Define associations (Xác định liên kết - Ràng buộc)

Product.belongsTo(ProductCategory, {
  foreignKey: "category_id",
  as: "category",
});
ProductCategory.hasMany(Product, { foreignKey: "category_id", as: "products" });

Product.belongsTo(Discount, { foreignKey: "discount_id", as: "discount" });
Discount.hasMany(Product, { foreignKey: "discount_id", as: "products" });

Product.hasMany(ProductImages, { foreignKey: "product_id", as: "images" });
ProductImages.belongsTo(Product, { foreignKey: "product_id", as: "product" });

Product.hasMany(ProductVariant, { foreignKey: "product_id", as: "variants" });
ProductVariant.belongsTo(Product, { foreignKey: "product_id", as: "product" });
ProductVariant.belongsTo(ProductColor, { foreignKey: "color_id", as: "color" });
ProductVariant.belongsTo(ProductSize, { foreignKey: "size_id", as: "size" });

User.hasMany(Order, { foreignKey: "user_id", as: "orders" });
Order.belongsTo(User, { foreignKey: "user_id", as: "user" });

Order.hasMany(OrderDetail, { foreignKey: "order_id", as: "details" });
OrderDetail.belongsTo(Order, { foreignKey: "order_id", as: "order" });
OrderDetail.belongsTo(ProductVariant, {
  foreignKey: "product_variant_id",
  as: "variant",
});

User.hasMany(CustomerAddress, { foreignKey: "user_id", as: "addresses" });
CustomerAddress.belongsTo(User, { foreignKey: "user_id", as: "user" });

User.hasMany(CustomerPayment, { foreignKey: "user_id", as: "payments" });
CustomerPayment.belongsTo(User, { foreignKey: "user_id", as: "user" });

User.hasMany(Blog, { foreignKey: "author_id", as: "blogs" });
Blog.belongsTo(User, { foreignKey: "author_id", as: "author" });

User.hasMany(Wishlist, { foreignKey: "user_id", as: "wishlists" });
Wishlist.belongsTo(User, { foreignKey: "user_id", as: "user" });
Wishlist.belongsTo(Product, { foreignKey: "product_id", as: "product" });

Product.hasMany(ProductReview, { foreignKey: "product_id", as: "reviews" });
ProductReview.belongsTo(Product, { foreignKey: "product_id", as: "product" });
User.hasMany(ProductReview, { foreignKey: "user_id", as: "reviews" });
ProductReview.belongsTo(User, { foreignKey: "user_id", as: "user" });

// Synchronize all models with the database
sequelize
  .sync({ alter: true })
  .then(() => console.log("Database & tables created!"))
  .catch((error) => console.log("This error occurred", error));

export {
  ProductCategory,
  Discount,
  Product,
  ProductImages,
  ProductColor,
  ProductSize,
  ProductVariant,
  User,
  Order,
  OrderDetail,
  CustomerAddress,
  CustomerPayment,
  Blog,
  Wishlist,
  ProductReview,
  sequelize,
  Sequelize,
};

